/* FounderPage.css */
.founder-container {
  padding: 2rem;
  background-color: #ffffff;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
}

.fade-slide {
  animation: fadeSlideIn 0.5s ease forwards;
}

@keyframes fadeSlideIn {
  0% {
    opacity: 0;
    transform: translateY(20px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

.founder-title {
  font-size: 2rem;
  color: #333;
  margin-bottom: 1rem;
}

.founder-bio {
  color: #555;
  margin-bottom: 2rem;
  max-width: 800px;
  text-align: justify;
}

.founder-section-title {
  font-size: 1.4rem;
  margin-top: 2rem;
  margin-bottom: 1rem;
  color: #222;
  border-bottom: 2px solid #1976d2;
  display: inline-block;
  width: 100%;
  max-width: 800px;
}

.founder-experience-list {
  display: flex;
  flex-wrap: wrap;
  gap: 1rem;
  justify-content: center;
  max-width: 800px;
}

.founder-experience-card {
  background-color: #fff;
  border: 1px solid #ddd;
  border-radius: 6px;
  padding: 1rem;
  flex: 1 1 280px;
  min-width: 280px;
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.07);
  transition: box-shadow 0.3s ease;
}
.founder-experience-card:hover {
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
}

.founder-experience-role {
  font-size: 1.1rem;
  margin-bottom: 0.2rem;
  color: #333;
}
.founder-experience-company {
  font-size: 1rem;
  margin-bottom: 0.2rem;
  color: #1976d2;
}
.founder-experience-date {
  font-size: 0.9rem;
  margin-bottom: 0.5rem;
  color: #666;
}
.founder-experience-desc {
  font-size: 0.9rem;
  color: #444;
}

.founder-education {
  margin-top: 0.5rem;
  color: #444;
  max-width: 800px;
  text-align: left;
}

.founder-btn {
  display: inline-block;
  background-color: #1976d2;
  color: #fff;
  padding: 0.6rem 1.2rem;
  border-radius: 4px;
  text-decoration: none;
  margin-top: 2rem;
  transition: background-color 0.3s ease;
}
.founder-btn:hover {
  background-color: #145ca1;
}
