/* HomePage.css */
.home-container {
  padding: 2rem;
  color: #fff;
  min-height: 100vh;
  background: #121212;
  font-family: sans-serif;
  animation: fadeInSlideUp 0.8s ease forwards;
}

@keyframes fadeInSlideUp {
  0% {
    opacity: 0;
    transform: translateY(20px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

.home-title {
  margin-bottom: 1rem;
  font-size: 2rem;
}

.home-subtitle {
  margin-bottom: 2rem;
  font-size: 1.2rem;
  color: #aaa;
}

.advanced-filter {
  background-color: rgba(255, 255, 255, 0.08);
  border: 1px solid rgba(255, 255, 255, 0.15);
  border-radius: 8px;
  padding: 1rem;
  margin-bottom: 2rem;
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.filter-row {
  display: flex;
  align-items: center;
  gap: 0.5rem;
}

.home-card {
  background-color: rgba(255, 255, 255, 0.05);
  padding: 1rem;
  margin-bottom: 1rem;
  border-radius: 6px;
  border: 1px solid rgba(255, 255, 255, 0.15);
}

.home-card-title {
  margin-top: 0;
  font-size: 1.4rem;
  margin-bottom: 0.5rem;
}

.home-card-founders {
  margin-bottom: 1rem;
  color: #bbb;
}

.home-btn {
  display: inline-block;
  padding: 0.5rem 1rem;
  background: linear-gradient(135deg, #ff00aa, #00aaff);
  color: #fff;
  border-radius: 4px;
  text-decoration: none;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.home-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
}

/* Responsive Styles */
@media (max-width: 768px) {
  .home-container {
    padding: 1rem;
  }

  .home-title {
    font-size: 1.8rem;
  }

  .home-subtitle {
    font-size: 1rem;
  }

  .advanced-filter {
    padding: 0.8rem;
    margin-bottom: 1rem;
  }

  .
