/* src/pages/NoLinkedInPage.css */
.no-linkedin-container {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 3rem;
    min-height: 100vh;
    background: linear-gradient(135deg, #e0f7fa, #ffffff);
    text-align: center;
  }
  
  .animation-wrapper {
    animation: slideFadeIn 1s ease forwards;
  }
  
  @keyframes slideFadeIn {
    from {
      opacity: 0;
      transform: translateY(-20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
  
  .creative-title {
    font-size: 2.8rem;
    color: #00796b;
    margin-bottom: 1rem;
  }
  
  .creative-subtitle {
    font-size: 1.5rem;
    color: #004d40;
    margin-bottom: 1rem;
  }
  
  .creative-text {
    font-size: 1.2rem;
    color: #00695c;
    margin-bottom: 2rem;
  }
  
  .back-home-btn {
    background-color: #00796b;
    color: #fff;
    padding: 0.8rem 1.6rem;
    border-radius: 8px;
    text-decoration: none;
    font-size: 1.1rem;
    transition: background 0.3s ease, transform 0.3s ease;
  }
  
  .back-home-btn:hover {
    background-color: #004d40;
    transform: translateY(-3px);
  }
