+// src/components/charts/ExperienceChart.js
+import React from 'react';
+import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
+import './ExperienceChart.css'; // chart-specific styling
+
+function ExperienceChart({ experiences }) {
+  // We'll transform each experience into data for the chart
+  // e.g. totalMonths is a rough estimate
+  const data = experiences.map((exp) => {
+    // naive parse for total months
+    let totalMonths = 12; // default fallback
+    if (exp.Date && exp.Date.includes('-')) {
+      // e.g. "Oct 2020 - Present" -> parse or approximate
+      if (exp.Date.toLowerCase().includes('present')) {
+        totalMonths = 36; 
+      } else {
+        totalMonths = 24; 
+      }
+    }
+    return {
+      role: exp.Role || 'N/A',
+      months: totalMonths,
+      company: exp.Company || 'N/A'
+    };
+  });
+
+  return (
+    <div className="chart-container">
+      <ResponsiveContainer width="100%" height={300}>
+        <BarChart data={data} className="bar-chart">
+          <CartesianGrid strokeDasharray="3 3" />
+          <XAxis dataKey="role" />
+          <YAxis />
+          <Tooltip />
+          <Bar dataKey="months" fill="#8884d8" animationDuration={800} />
+        </BarChart>
+      </ResponsiveContainer>
+    </div>
+  );
+}
+
+export default ExperienceChart;

