import React from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

function ExperienceChart({ experiences }) {
  // For demonstration, let’s transform experiences into a data array with year range or total months
  // Example: { Role: 'CEO', Company: '100ms', totalMonths: 36 }

  const data = experiences.map((exp) => {
    const dateRange = exp.Date || '';
    // Very naive parse to get approximate total months:
    // (In real code, you might parse actual start/end year/month)
    let totalMonths = 0;
    if (dateRange.includes('-')) {
      const parts = dateRange.split('-');
      // e.g. "Oct 2020 - Present" -> approximate
      if (parts[1].toLowerCase().includes('present')) {
        totalMonths = 36; // Hard-coded for demonstration
      } else {
        totalMonths = 24; // Hard-coded example
      }
    }

    return {
      role: exp.Role,
      months: totalMonths,
      company: exp.Company
    };
  });

  return (
    <ResponsiveContainer width="100%" height={300}>
      <BarChart data={data} margin={{ top: 20, right: 20, left: 0, bottom: 20 }}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="role" />
        <YAxis />
        <Tooltip />
        <Bar dataKey="months" fill="#8884d8" />
      </BarChart>
    </ResponsiveContainer>
  );
}

export default ExperienceChart;
