/* Dark background with starfield effect */
.home-container {
    min-height: 100vh;
    padding: 2rem;
    margin: 0 auto;
    max-width: 1200px;
    background: #0b0f1f; /* Dark navy */
    /* Create starfield using repeated radial gradients or pseudo-element */
    background-image:
      radial-gradient(2px 2px at 20% 20%, #fff 2%, transparent 2%),
      radial-gradient(1px 1px at 60% 30%, #fff 1%, transparent 1%);
    background-size: 100px 100px, 100px 100px;
    background-repeat: repeat;
    font-family: 'Roboto', sans-serif;
  }
  
  /* Fade in + slide up animation */
  @keyframes fadeInUpHome {
    0% {
      opacity: 0;
      transform: translateY(15px);
    }
    100% {
      opacity: 1;
      transform: translateY(0);
    }
  }
  
  .fade-in-up {
    animation: fadeInUpHome 0.8s ease forwards;
  }
  
  /* Title and subtitle styling */
  .home-title {
    font-size: 2rem;
    color: #fff;
    margin-bottom: 1rem;
  }
  
  .home-subtitle {
    font-size: 1.1rem;
    color: #cfcfcf;
    margin-bottom: 2rem;
  }
  
  /* Card styling */
  .home-card {
    background-color: rgba(255, 255, 255, 0.07);
    border: 1px solid rgba(255, 255, 255, 0.2);
    padding: 1.5rem;
    border-radius: 6px;
    margin-bottom: 1.5rem;
    transition: box-shadow 0.3s ease, transform 0.3s ease;
  }
  
  .home-card:hover {
    box-shadow: 0 4px 15px rgba(255, 255, 255, 0.15);
    transform: translateY(-2px);
  }
  
  /* Card text */
  .home-card-title {
    font-size: 1.3rem;
    margin-bottom: 0.5rem;
    color: #fff;
  }
  
  .home-card-founders {
    color: #ddd;
    margin-bottom: 1rem;
  }
  
  /* Button styling */
  .home-btn {
    display: inline-block;
    background-color: #4f6ef7;
    color: #fff;
    padding: 0.6rem 1.2rem;
    border-radius: 4px;
    text-decoration: none;
    font-size: 0.95rem;
    transition: background-color 0.3s ease, transform 0.3s ease;
  }
  
  .home-btn:hover {
    background-color: #2c4bd1;
    transform: translateY(-1px);
  }
