import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { companies } from '../data/sampleData';
import { Typography, Button, Box } from '@mui/material';

function CompanyPage() {
  const { companyName } = useParams();

  // Find the relevant company data
  const company = companies.find(
    (c) => c['company-name'] === decodeURIComponent(companyName)
  );

  if (!company) {
    return (
      <div style={{ padding: '2rem' }}>
        <Typography variant="h5">Company not found!</Typography>
      </div>
    );
  }

  const founders = company['founder-names'];
  
  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>
        {company['company-name']}
      </Typography>
      <Typography variant="body1" paragraph>
        Founders:
      </Typography>
      {founders.map((founder) => (
        <Button
          key={founder}
          variant="outlined"
          sx={{ marginRight: 1, marginBottom: 1 }}
          component={Link}
          to={`/founder/${encodeURIComponent(founder)}`}
        >
          {founder}
        </Button>
      ))}

      {/* 
        Example: You might display some aggregated stats here in a chart. 
        For a truly advanced experience, you’d feed data into a Recharts or 
        Chart.js component to visualize the company's overall metrics.
      */}
    </Box>
  );
}

export default CompanyPage;
