   @page {
     margin: 2cm;
   }
 }
+
+/* Accessibility features */
+.visually-hidden {
+  position: absolute;
+  width: 1px;
+  height: 1px;
+  padding: 0;
+  margin: -1px;
+  overflow: hidden;
+  clip: rect(0, 0, 0, 0);
+  white-space: nowrap;
+  border-width: 0;
+}
+
+.focus-visible:focus {
+  outline: 2px solid var(--primary);
+  outline-offset: 2px;
+}
+
+/* Data visualization specific styles */
+.chart-container {
+  width: 100%;
+  height: 300px;
+  background-color: var(--neutral-100);
+  border-radius: var(--radius-md);
+  box-shadow: var(--shadow-md);
+  padding: var(--space-lg);
+  margin-bottom: var(--space-xl);
+}
+
+.chart-header {
+  display: flex;
+  justify-content: space-between;
+  align-items: center;
+  margin-bottom: var(--space-md);
+}
+
+.chart-title {
+  font-size: 1rem;
+  font-weight: 600;
+  color: var(--neutral-800);
+}
+
+.chart-legend {
+  display: flex;
+  gap: var(--space-md);
+}
+
+.chart-legend-item {
+  display: flex;
+  align-items: center;
+  font-size: 0.75rem;
+  color: var(--neutral-600);
+}
+
+.chart-legend-color {
+  width: 12px;
+  height: 12px;
+  border-radius: 2px;
+  margin-right: var(--space-xs);
+}
+
+/* Project Kalam specific styles */
+.kalam-header {
+  background-color: var(--primary-dark);
+  color: var(--neutral-100);
+  padding: var(--space-xl) 0;
+  margin-bottom: var(--space-xl);
+}
+
+.kalam-header-content {
+  max-width: 1280px;
+  margin: 0 auto;
+  padding: 0 var(--space-lg);
+}
+
+.kalam-title {
+  font-size: 2.5rem;
+  font-weight: 700;
+  margin-bottom: var(--space-md);
+}
+
+.kalam-subtitle {
+  font-size: 1.25rem;
+  max-width: 600px;
+  margin-bottom: var(--space-lg);
+  opacity: 0.9;
+}
+
+.kalam-stats {
+  display: flex;
+  gap: var(--space-xl);
+  margin-top: var(--space-xl);
+}
+
+.kalam-stat {
+  text-align: center;
+}
+
+.kalam-stat-value {
+  font-size: 2.5rem;
+  font-weight: 700;
+  margin-bottom: var(--space-xs);
+}
+
+.kalam-stat-label {
+  text-transform: uppercase;
+  letter-spacing: 0.05em;
+  font-size: 0.875rem;
+  opacity: 0.8;
+}
+
+/* Talent search specific styles */
+.search-filters {
+  background-color: var(--neutral-100);
+  border-radius: var(--radius-md);
+  box-shadow: var(--shadow-md);
+  padding: var(--space-lg);
+  margin-bottom: var(--space-xl);
+}
+
+.filter-group {
+  margin-bottom: var(--space-md);
+}
+
+.filter-label {
+  font-size: 0.875rem;
+  font-weight: 500;
+  margin-bottom: var(--space-xs);
+  color: var(--neutral-700);
+}
+
+.filter-slider {
+  width: 100%;
+  height: 6px;
+  background-color: var(--neutral-300);
+  border-radius: var(--radius-sm);
+  position: relative;
+  margin-top: var(--space-md);
+  margin-bottom: var(--space-md);
+}
+
+.filter-slider-fill {
+  position: absolute;
+  top: 0;
+  left: 0;
+  height: 100%;
+  background-color: var(--primary);
+  border-radius: var(--radius-sm);
+}
+
+.filter-slider-handle {
+  position: absolute;
+  top: 50%;
+  width: 16px;
+  height: 16px;
+  background-color: var(--primary);
+  border: 2px solid var(--neutral-100);
+  border-radius: 50%;
+  transform: translate(-50%, -50%);
+  box-shadow: var(--shadow-sm);
+  cursor: pointer;
+  transition: transform var(--transition-fast), box-shadow var(--transition-fast);
+}
+
+.filter-slider-handle:hover {
+  transform: translate(-50%, -50%) scale(1.1);
+  box-shadow: var(--shadow-md);
+}
+
+.filter-slider-values {
+  display: flex;
+  justify-content: space-between;
+  font-size: 0.75rem;
+  color: var(--neutral-600);
+}
+
+.filter-chips {
+  display: flex;
+  flex-wrap: wrap;
+  gap: var(--space-xs);
+  margin-top: var(--space-sm);
+}
+
+.filter-chip {
+  display: inline-flex;
+  align-items: center;
+  background-color: rgba(11, 83, 148, 0.1);
+  color: var(--primary);
+  padding: var(--space-xs) var(--space-sm);
+  border-radius: var(--radius-sm);
+  font-size: 0.75rem;
+  cursor: pointer;
+  transition: all var(--transition-fast);
+}
+
+.filter-chip:hover {
+  background-color: rgba(11, 83, 148, 0.2);
+}
+
+.filter-chip-remove {
+  margin-left: var(--space-xs);
+  width: 14px;
+  height: 14px;
+  border-radius: 50%;
+  background-color: rgba(0, 0, 0, 0.1);
+  display: flex;
+  align-items: center;
+  justify-content: center;
+  font-size: 10px;
+}
+
+/* Search results */
+.search-results {
+  margin-bottom: var(--space-xl);
+}
+
+.search-result-card {
+  background-color: var(--neutral-100);
+  border-radius: var(--radius-md);
+  box-shadow: var(--shadow-md);
+  padding: var(--space-lg);
+  margin-bottom: var(--space-md);
+  transition: transform var(--transition-normal), box-shadow var(--transition-normal);
+  display: flex;
+  align-items: flex-start;
+}
+
+.search-result-card:hover {
+  transform: translateY(-2px);
+  box-shadow: var(--shadow-lg);
+}
+
+.search-result-score {
+  flex-shrink: 0;
+  width: 60px;
+  height: 60px;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+  background-color: rgba(11, 83, 148, 0.1);
+  color: var(--primary);
+  font-size: 1.5rem;
+  font-weight: 700;
+  border-radius: var(--radius-md);
+  margin-right: var(--space-lg);
+}
+
+.search-result-content {
+  flex: 1;
+}
+
+.search-result-name {
+  font-size: 1.25rem;
+  font-weight: 600;
+  color: var(--neutral-900);
+  margin-bottom: var(--space-xs);
+}
+
+.search-result-position {
+  color: var(--neutral-700);
+  margin-bottom: var(--space-xs);
+}
+
+.search-result-skills {
+  display: flex;
+  flex-wrap: wrap;
+  gap: var(--space-xs);
+  margin-top: var(--space-sm);
+  margin-bottom: var(--space-sm);
+}
+
+.search-result-skill {
+  background-color: var(--neutral-200);
+  padding: var(--space-xs) var(--space-sm);
+  border-radius: var(--radius-sm);
+  font-size: 0.75rem;
+  color: var(--neutral-700);
+}
+
+.search-result-metrics {
+  display: flex;
+  gap: var(--space-lg);
+  margin-top: var(--space-md);
+}
+
+.search-result-metric {
+  display: flex;
+  align-items: center;
+  font-size: 0.875rem;
+  color: var(--neutral-600);
+}
+
+.search-result-metric-icon {
+  margin-right: var(--space-xs);
+  color: var(--primary);
+}
+
+.search-result-actions {
+  margin-left: var(--space-md);
+  display: flex;
+  flex-direction: column;
+  gap: var(--space-sm);
+}
+
+/* Machine learning score visualization */
+.ml-score-ring {
+  position: relative;
+  width: 120px;
+  height: 120px;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+  margin: 0 auto var(--space-lg);
+}
+
+.ml-score-circle {
+  position: absolute;
+  width: 100%;
+  height: 100%;
+  border-radius: 50%;
+  background: conic-gradient(
+    var(--primary) 0% calc(var(--score-percentage) * 1%),
+    var(--neutral-300) calc(var(--score-percentage) * 1%) 100%
+  );
+  transform: rotate(-90deg);
+}
+
+.ml-score-inner {
+  position: absolute;
+  width: 80%;
+  height: 80%;
+  background-color: var(--neutral-100);
+  border-radius: 50%;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+  font-size: 1.5rem;
+  font-weight: 700;
+  color: var(--primary);
+}
+
+/* Factor contribution visualization */
+.factor-container {
+  margin-bottom: var(--space-md);
+}
+
+.factor-header {
+  display: flex;
+  justify-content: space-between;
+  margin-bottom: var(--space-xs);
+}
+
+.factor-name {
+  font-weight: 500;
+  color: var(--neutral-700);
+}
+
+.factor-value {
+  color: var(--primary);
+  font-weight: 600;
+}
+
+.factor-bar {
+  height: 8px;
+  background-color: var(--neutral-300);
+  border-radius: var(--radius-sm);
+  overflow: hidden;
+}
+
+.factor-bar-fill {
+  height: 100%;
+  background-color: var(--primary);
+  border-radius: var(--radius-sm);
+}
+
+/* Network visualization styles */
+.network-controls {
+  display: flex;
+  justify-content: space-between;
+  margin-bottom: var(--space-md);
+}
+
+.network-legend {
+  display: flex;
+  gap: var(--space-md);
+}
+
+.network-legend-item {
+  display: flex;
+  align-items: center;
+  font-size: 0.75rem;
+  color: var(--neutral-600);
+}
+
+.network-node {
+  fill: var(--primary);
+  transition: all var(--transition-fast);
+}
+
+.network-node:hover {
+  fill: var(--primary-dark);
+  cursor: pointer;
+}
+
+.network-label {
+  font-size: 10px;
+  fill: var(--neutral-700);
+  text-anchor: middle;
+  pointer-events: none;
+}
+
+.network-link {
+  stroke: var(--neutral-400);
+  stroke-opacity: 0.6;
+}
+
+/* Document repository styles */
+.document-list {
+  display: grid;
+  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
+  gap: var(--space-md);
+}
+
+.document-item {
+  background-color: var(--neutral-100);
+  border-radius: var(--radius-md);
+  padding: var(--space-md);
+  box-shadow: var(--shadow-sm);
+  transition: all var(--transition-normal);
+  cursor: pointer;
+}
+
+.document-item:hover {
+  transform: translateY(-2px);
+  box-shadow: var(--shadow-md);
+}
+
+.document-icon {
+  font-size: 2rem;
+  color: var(--primary);
+  margin-bottom: var(--space-sm);
+}
+
+.document-name {
+  font-weight: 500;
+  margin-bottom: var(--space-xs);
+  word-break: break-word;
+}
+
+.document-meta {
+  font-size: 0.75rem;
+  color: var(--neutral-600);
+}
+
+/* Project-specific utility classes */
+.text-primary {
+  color: var(--primary);
+}
+
+.text-secondary {
+  color: var(--secondary);
+}
+
+.text-success {
+  color: #27ae60;
+}
+
+.text-warning {
+  color: #f1c40f;
+}
+
+.text-danger {
+  color: #e74c3c;
+}
+
+.bg-primary-light {
+  background-color: rgba(11, 83, 148, 0.1);
+}
+
+.border-primary {
+  border-color: var(--primary);
+}
+
+.border-left-primary {
+  border-left: 3px solid var(--primary);
+}
+
+.shadow-hover {
+  transition: box-shadow var(--transition-normal);
+}
+
+.shadow-hover:hover {
+  box-shadow: var(--shadow-lg);
+}
+
+.cursor-pointer {
+  cursor: pointer;
+}
+
+/* Additional Project Kalam specific components */
+.talent-category-card {
+  background-color: var(--neutral-100);
+  border-radius: var(--radius-md);
+  box-shadow: var(--shadow-md);
+  padding: var(--space-lg);
+  border-top: 4px solid var(--primary);
+  transition: transform var(--transition-normal), box-shadow var(--transition-normal);
+  height: 100%;
+}
+
+.talent-category-card:hover {
+  transform: translateY(-4px);
+  box-shadow: var(--shadow-lg);
+}
+
+.talent-category-icon {
+  font-size: 2rem;
+  color: var(--primary);
+  margin-bottom: var(--space-md);
+}
+
+.talent-category-title {
+  font-size: 1.25rem;
+  font-weight: 600;
+  margin-bottom: var(--space-sm);
+}
+
+.talent-category-count {
+  color: var(--primary);
+  font-weight: 700;
+  font-size: 1.5rem;
+  margin-bottom: var(--space-md);
+}
+
+.talent-category-trend {
+  display: flex;
+  align-items: center;
+  font-size: 0.875rem;
+}
+
+.talent-category-trend-positive {
+  color: var(--secondary);
+}
+
+.talent-category-trend-negative {
+  color: #e74c3c;
+}

